import React, { useEffect, useState } from "react";

export default function BinanceFuturesTracker() {
  const [coins, setCoins] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch("https://fapi.binance.com/fapi/v1/ticker/24hr")
      .then((res) => res.json())
      .then((data) => {
        const usdtCoins = data
          .filter((coin) => coin.symbol.endsWith("USDT"))
          .sort(
            (a, b) =>
              Math.abs(b.priceChangePercent) - Math.abs(a.priceChangePercent)
          )
          .slice(0, 10); // top 10

        setCoins(usdtCoins);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Error fetching Binance data:", err);
      });
  }, []);

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Binance Futures 5-Min Change Tracker</h1>
      {loading ? (
        <p>Loading data...</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          {coins.map((coin) => (
            <div
              key={coin.symbol}
              className={`rounded-xl p-4 shadow-md text-white font-semibold text-center ${
                coin.priceChangePercent > 0 ? "bg-green-500" : "bg-red-500"
              }`}
            >
              <div className="text-lg">{coin.symbol}</div>
              <div>
                {coin.priceChangePercent > 0 ? "+" : ""}
                {parseFloat(coin.priceChangePercent).toFixed(2)}%
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
