import React, { useEffect, useState } from "react";

const INTERVAL = 30000; // 30 —Å–µ–∫—É–Ω–¥ —Ç—É—Ç–∞–º–¥ —à–∏–Ω—ç—á–∏–ª–Ω—ç

export default function FuturesTopMovers() {
  const [topGainers, setTopGainers] = useState([]);
  const [topLosers, setTopLosers] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchData = async () => {
    try {
      const res = await fetch("https://fapi.binance.com/fapi/v1/ticker/price");
      const allCoins = await res.json();

      const usdtCoins = allCoins.filter((coin) =>
        coin.symbol.endsWith("USDT")
      );

      const results = [];

      for (const coin of usdtCoins.slice(0, 100)) { // —ç—Ö–Ω–∏–π 100-–≥ —à–∞–ª–≥–∞–Ω–∞
        try {
          const klineRes = await fetch(
            `https://fapi.binance.com/fapi/v1/klines?symbol=${coin.symbol}&interval=1m&limit=5`
          );
          const klines = await klineRes.json();

          if (klines.length < 2) continue;

          const open = parseFloat(klines[0][1]);
          const close = parseFloat(klines[klines.length - 1][4]);
          const change = ((close - open) / open) * 100;

          results.push({ symbol: coin.symbol, change });
        } catch (_) {}
      }

      const sorted = results.sort((a, b) => b.change - a.change);
      setTopGainers(sorted.slice(0, 10));
      setTopLosers(sorted.slice(-10).reverse());
      setLoading(false);
    } catch (error) {
      console.error("Fetch error:", error);
    }
  };

  useEffect(() => {
    fetchData();
    const interval = setInterval(fetchData, INTERVAL);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Binance Futures 5-Min Movers</h1>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <h2 className="text-lg font-bold mb-2">üìà Gainers</h2>
            {topGainers.map((coin) => (
              <div
                key={coin.symbol}
                className="bg-green-500 text-white p-3 rounded-xl shadow-md mb-2"
              >
                {coin.symbol}: {coin.change.toFixed(2)}%
              </div>
            ))}
          </div>
          <div>
            <h2 className="text-lg font-bold mb-2">üìâ Losers</h2>
            {topLosers.map((coin) => (
              <div
                key={coin.symbol}
                className="bg-red-500 text-white p-3 rounded-xl shadow-md mb-2"
              >
                {coin.symbol}: {coin.change.toFixed(2)}%
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}
